import { apiKey, apiUrl } from "../../common/api";

export const getMovies = async ({ queryKey }) => {
  const [, { page, query, language }] = queryKey;

  const params = new URLSearchParams({
    language,
    page: page || 1,
    ...(query && { query }),
  });

  const url = `${apiUrl}${'fruits'}`;

  const response = await fetch(url);

  if (!response.ok) {
    throw new Error(response.statusText);
  }

  return [
    {
        "id": 1,
        "name": "Pomme",
        "image_url": "https://exemple.com/pomme.jpg",
        "smart_contract": "KT1abc123"
    },
    {
        "id": 2,
        "name": "Banane",
        "image_url": "https://exemple.com/banane.jpg",
        "smart_contract": "KT1def456"
    },
    {
        "id": 3,
        "name": "Orange",
        "image_url": "https://exemple.com/orange.jpg",
        "smart_contract": "KT1ghi789"
    },
    {
        "id": 4,
        "name": "Fraise",
        "image_url": "https://exemple.com/fraise.jpg",
        "smart_contract": "KT1jkl012"
    },
    {
        "id": 5,
        "name": "Raisin",
        "image_url": "https://exemple.com/raisin.jpg",
        "smart_contract": "KT1mno345"
    }
]
;
};

export const getGenres = async ({ queryKey }) => {
  const [, { language }] = queryKey;

  const response = await fetch(
    `${apiUrl}genre/movie/list?api_key=${apiKey}&language=${language}`
  );

  if (!response.ok) {
    throw new Error(response.statusText);
  }

  const data = await response.json();

  return data.genres;
};

export const getMovieDetails = async ({ queryKey }) => {
  const [, { id, language }] = queryKey;

  const response = await fetch(
    `${apiUrl}movie/${id}?api_key=${apiKey}&language=${language}`
  );

  if (!response.ok) {
    throw new Error(response.statusText);
  }

  return await response.json();
};

export const getMovieCredits = async ({ queryKey }) => {
  const [, { id, language }] = queryKey;

  const response = await fetch(
    `${apiUrl}movie/${id}/credits?api_key=${apiKey}&language=${language}`
  );

  if (!response.ok) {
    throw new Error(response.statusText);
  }

  return await response.json();
};
