import React from "react";
import star from "./ratingStar.svg";
import Tile from "../../../common/Tile";
import Poster from "../../../images/Poster.svg";
import {
  FoodTileImg,
  FoodTileHeader,
  MovieTileYear,
  MovieTileTags,
  MovieAdditionalInfo,
  MovieRatingImg,
  MovieRatingText,
  MovieRatingVotes,
  FoodInfoWrapper,
} from "./styled";
import { Link } from "react-router-dom";
import { toFood } from "../../../routes";
import { votes } from "../../../common/languages";
import { selectLanguage } from "../../../common/Navigation/LanguageSelect/languageSlice";
import { useSelector } from "react-redux";
import { useGenres } from "./useGenres";

const MovieTile = ({ food }) => {
  const language = useSelector(selectLanguage);
  const generatedTags = useGenres(food.genre_ids);

  return (
    <Tile as={Link} to={toFood({ id: food.id })}>
      <FoodTileImg
        url={
          Poster
        }
      />
      <FoodInfoWrapper>
        <FoodTileHeader>{food.title}</FoodTileHeader>
        <MovieTileYear>
          {food.character
            ? `${food.character} (${
                food.release_date ? food.release_date.slice(0, 4) : "????"
              })`
            : food.job
            ? `${food.job} (${
                food.release_date ? food.release_date.slice(0, 4) : "????"
              })`
            : food.release_date
            ? food.release_date.slice(0, 4)
            : "????"}
        </MovieTileYear>
        <MovieTileTags>{generatedTags}</MovieTileTags>
        <MovieAdditionalInfo>
          <MovieRatingImg src={star}></MovieRatingImg>
          <MovieRatingText>{food.vote_average.toFixed(1)}</MovieRatingText>
          <MovieRatingVotes>
            {(Math.random() * 10+2).toFixed(0)} {votes[language]}
          </MovieRatingVotes>
        </MovieAdditionalInfo>
      </FoodInfoWrapper>
    </Tile>
  );
};

export default MovieTile;
