import React from "react";
import star from "./ratingStar.svg";
import Tile from "../../../common/Tile";
import Poster from "../../../images/Poster.svg";
import {
  MovieTileImg,
  MovieTileHeader,
  MovieTileYear,
  MovieTileTags,
  MovieAdditionalInfo,
  MovieRatingImg,
  MovieRatingText,
  MovieRatingVotes,
  MovieInfoWrapper,
} from "./styled";
import { Link } from "react-router-dom";
import { toFood } from "../../../routes";
import { votes } from "../../../common/languages";
import { selectLanguage } from "../../../common/Navigation/LanguageSelect/languageSlice";
import { useSelector } from "react-redux";
import { useGenres } from "./useGenres";

const MovieTile = ({ movie }) => {
  const language = useSelector(selectLanguage);
  const generatedTags = useGenres(movie.genre_ids);

  return (
    <Tile as={Link} to={toFood({ id: movie.id })}>
      <MovieTileImg
        url={
          Poster
        }
      />
      <MovieInfoWrapper>
        <MovieTileHeader>{movie.title}</MovieTileHeader>
        <MovieTileYear>
          {movie.character
            ? `${movie.character} (${
                movie.release_date ? movie.release_date.slice(0, 4) : "????"
              })`
            : movie.job
            ? `${movie.job} (${
                movie.release_date ? movie.release_date.slice(0, 4) : "????"
              })`
            : movie.release_date
            ? movie.release_date.slice(0, 4)
            : "????"}
        </MovieTileYear>
        <MovieTileTags>{generatedTags}</MovieTileTags>
        <MovieAdditionalInfo>
          <MovieRatingImg src={star}></MovieRatingImg>
          <MovieRatingText>{movie.vote_average.toFixed(1)}</MovieRatingText>
          <MovieRatingVotes>
            {movie.vote_count} {votes[language]}
          </MovieRatingVotes>
        </MovieAdditionalInfo>
      </MovieInfoWrapper>
    </Tile>
  );
};

export default MovieTile;
